    private void DocenteBoni() {
      Docente d = new Docente();
      int horas = Integer.parseInt(txthoras.getText()); 
      d.setNhoras(horas);
      d.CalculoSueldo();
      d.Bonificacion();
      double repo = d.Bonif; 
      JOptionPane.showMessageDialog(rootPane,"La bonificaci√≥n del docente es "+ repo);  
    }
      







